package main

import "fmt"

func main() {
	input := ""

	for i := 0; i < len(input); i++ {
		count := [26]int{}

		i1 := input[i] - 'a'
		i2 := input[i+1] - 'a'
		i3 := input[i+2] - 'a'
		i4 := input[i+3] - 'a'

		count[i1] += 1
		count[i2] += 1
		count[i3] += 1
		count[i4] += 1

		if count[i1]+count[i2]+count[i3]+count[i4] == 4 {
			fmt.Println(i + 4)
			break
		}
	}
}
